import react, { createRef } from "react";
import { useState, useEffect, useRef } from "react";
import MessageUnit from "./messageUnite";
import axios from "axios";

const DiscussionContent = (props) => {
    const messagesEndRef = createRef(null);
    const [user, setUser] = useState(props.user);
    const [fdata, setFdata] = useState();
    const [ready, setReady] = useState(false);
    async function getData(user){
        let final = [];
        let data = await axios.get("http://localhost:3000/messages/")
        .then(
            response=>{
                return(response.data);
            }
        )
        data.forEach((item) => {
            if (item.from === props.user.name || item.to === props.user.name){
                final.push(item);
            }
        });
        setFdata(final)
        return(final)
    };
    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" })
    }
    useEffect(() => {
        getData();
    }, [])
    useEffect(() => {
        if (messagesEndRef.current) {
          messagesEndRef.current.scrollIntoView(
            {
              behavior: 'smooth',
              block: 'end',
              inline: 'nearest'
            })
        }
      })
    const handleChange = (e) =>{
        console.log("a")
    };
    return(
        <div className="discussion-content">
            <div className="discussion-header bg-dark">
                Name
            </div>
            <div className="discussion-render">
                {useEffect(() => {
                    if (fdata !== undefined){
                        console.log(fdata);
                        setReady(true)
                    }
                }, [fdata])}
                {(fdata !== undefined) ? (<MessageUnit content={fdata[0].content} from={fdata[0].from} user={props.user}/>): (<div></div>)}
                {ready ? (fdata.filter((val)=> {return(val)}).map((val) => {return (<MessageUnit content={val.content} from={val.from} user={props.user}/>)})): (<div></div>)}
                <div ref={messagesEndRef} className="end-ref"/>
            </div>
            <div className="discussion-input bg-dark">
                <input className="discussion-inputbar form-control" type="text" onChange={handleChange}></input>
            </div>
        </div>
    )
}

export default DiscussionContent;
