import react, { useState } from "react";
import {Link} from "react-router-dom";
import styled from "styled-components";
import {BsFillChatLeftTextFill} from "react-icons/bs";
import {BiCaretDown} from "react-icons/bi";

const Navbar = (props) => {
    const StyledLink = styled(Link)`
    display: inline-block;
    position: relative;
    color:white;
    font-size: none;
    text-decoration: none;
    font-family: "Times New Roman", Times, serif;
    &:hover{
        color:white;
    }
    `;
    const handleClick = (e) => {
        console.log(e)
    }
    const [stylehome, setStylehome] = useState("link-div btn btn-primary");
    const [current, setCurrent] = useState(props.current);
    useEffect(() => {
        if (current == "home"){
            setStylehome(stylehome+"current")
            console.log(stylehome)
        }
    }, [current])
    
    
    return (
        <div>
            <div className="navglobal">
                <div className="logo">
                    <a href="">LOGO</a>
                </div>
                <div className="navcontainer navbar">
                    <div className=""><StyledLink to ="/home" className="nav-item">HomePage</StyledLink></div>
                    <div className="link-div btn btn-primary"><StyledLink to ="/home" onClick={handleClick} className="nav-item">Contact</StyledLink></div>
                    <div className="link-div btn btn-primary current"><StyledLink to ="/signin" className="nav-item">Sign in</StyledLink></div>
                    <div className="link-div">
                        <select class="form-select btn-primary" aria-label="Default select example">
                        <option selected>Open this select menu</option>
                        <option value="1">One</option>
                        <option value="2">Two</option>
                        <option value="3">Three</option>
                        </select>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Navbar;
