import react from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import {BsFillChatLeftDotsFill, BsFillChatLeftTextFill} from "react-icons/bs";
import styled from "styled-components";
import DiscussionContent from "./DiscussionContent";
import DiscItem from "./discItem";

const Messages = (props) => {
    const handleClick = () => {
        console.log("click")
    };
    const StyledLink = styled(Link)`
    display: inline-block;
    position: relative;
    color:white;
    font-size: none;
    text-decoration: none;
    font-family: "Times New Roman", Times, serif;
    &:hover{
        color:white;
        text-decoration: none;
    }
    &:after{
        color:white;
        text-decoration: none;
    }
    `;
    return(
        <div className="messages">
            <div className="conv bg-dark">
                <div className="header bg-dark"><BsFillChatLeftTextFill className="chat-icon"></BsFillChatLeftTextFill><div className="pad">Conversations</div></div>
                <div className="disc">
                    <DiscItem/>
                    <div className="disc-item"  onClick={handleClick}>
                        <div className="picture">
                        </div>
                        <div className="name">  
                            <button className="styledButton">Name</button>
                        </div>
                    </div>
                    <div className="disc-item"  onClick={handleClick}>
                        <div className="picture">
                        </div>
                        <div className="name">  
                            <button className="styledButton">Name</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className="sep">

            </div>
            <div className="mess bg-dark">
                <DiscussionContent/>
            </div>
        </div>
    )
}

export default Messages;
