import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import './App.css';
import React, { createContext, useEffect, useState } from 'react';
import HomePage from "./components/homepage";
import Navbar from "./components/navbar";
import SignIn from "./components/signin";
import {ThemeContext, themes} from './components/themeContext';
import Cache from "./components/cache";
import Messages from "./components/messages"

function App() {
  const [user, setUser] = useState();
  name1 = localStorage.getItem("name");
  if (name1 !== ""){
    setUser({name: name1})
  }
  useEffect(() => {
    console.log("a new name has been created")
  }, [user])
  return (
    <div className="App bg-dark">
      <BrowserRouter>
      <Cache/>
        <Navbar user={user}/>
        <Routes>
            <Route exact path="/home" element={<HomePage user={user} setUser={setUser}/>}/>
            <Route exact path="/messages" element={<Messages user={user} setUser={setUser}/>}/>
            <Route exact path="/signin" element={<SignIn user={user} setUser={setUser}/>}/>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
