import react from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { BsFillPersonFill } from "react-icons/bs";

const SignIn = () => { 
    const [signIn, setSignIn] = useState(true);
    const handleChange = () => {
        setSignIn(!signIn);
    }
    return (
        <form className="login form-group">
            <h1 className="title">{signIn ? "Sign in":"Sign up"}</h1>
            <hr size="3"></hr>
                {
                    signIn ? (
                        <div className="inputs">
                            Username : <div><div className="icon"><BsFillPersonFill className="icon-bs" size="20"></BsFillPersonFill></div><input className="text input no-outline" type="text"/></div><br/><br/>
                            Password : <input className="password form-control" type="password"/><br/><br/>
                            <Link to="" className="click-me" onClick={handleChange}>Don't have an account ? Sign up</Link>
                        </div>
                    ) : (
                        <div className="inputs">
                            Username : <input className="text form-control" type="text"/>
                            Mail : <input className="form-control" type="text"/>
                            Password : <input className="password form-control" type="password"/>
                            Confirm password : <input className="password form-control" type="password"/><br/>
                            <Link to="" className="click-me" onClick={handleChange}>Already have an account ? Sign in</Link>
                        </div>
                        )
                }
        </form>
    )
}

export default SignIn;
