import react from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { BsFillPersonFill, BsLockFill, BsEnvelope } from "react-icons/bs";

const SignIn = () => { 
    const [signIn, setSignIn] = useState(true);
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [mail, setMail] = useState("");
    const handleChange = () => {
        setSignIn(!signIn);
    }
    const handleChangeUsername = (e) => {
        setUsername(e.target.value);
        console.log(password);
    }

    return (
        <form className="login form-group">
            <div className="container">
            <h1 className="title">{signIn ? "Log in":"Sign up"}</h1>
            <hr size="3"></hr>
                {
                    signIn ? (
                        <div className="inputs">
                            Username : <div className="outline"><div className="icon-bg"><BsFillPersonFill className="icon" size="30"></BsFillPersonFill></div><input className="text input no-outline" type="text" onChange={handleChangeUsername}/></div><br/><br/>
                            Password : <div><div className="icon-bg"><BsLockFill className="icon" size="30"></BsLockFill></div><input className="password input no-outline" type="password"/></div><br/><br/>
                            <button type="submit" className="btn btn-primary">Log in</button><br/><br/>
                            <Link to="" className="click-me" onClick={handleChange}>Don't have an account ? Sign up</Link>
                        </div>
                    ) : (
                        <div className="inputs">
                            <div className="labels">
                            <div className="div-2"><div className="label2">Username : <br/></div><div className="outline div2"><div className="icon-bg"><BsFillPersonFill className="icon" size="30"></BsFillPersonFill></div><input className="text input no-outline" type="text"/></div><br/><br/></div>
                            <div className="div-2"><div className="label2">Mail : <br/></div><div className="outline div2"><div className="icon-bg"><BsEnvelope className="icon" size="30"></BsEnvelope></div><input className="text input no-outline" type="text"/></div><br/><br/></div></div>
                            Password : <div className="outline"><div className="icon-bg"><BsLockFill className="icon" size="30"></BsLockFill></div><input className="text input no-outline" type="text"/></div><br/><br/>
                            Confirm password : <div className="outline"><div className="icon-bg"><BsLockFill className="icon" size="30"></BsLockFill></div><input className="text input no-outline" type="text"/></div><br/><br/>
                            <button type="submit" className="btn btn-primary">Sign up</button><br/><br/>
                            <Link to="" className="click-me" onClick={handleChange}>Already have an account ? Sign in</Link>
                        </div>
                        )
                }
                </div>
        </form>
    )
}

export default SignIn;
