import react from "react";
import { useState, useEffect } from "react";

const MessageUnit = (props) =>{
    const [classname, setClassname] = useState(props.from);
    return(
        <div className={(classname == "user") ? ("message-unit user"): ("message-unit else")}>
            <div className="content bg-dark">{props.content}</div>
        </div>
    )
}

export default MessageUnit;
