import react, { useState, useEffect } from "react";
import {Link} from "react-router-dom";
import styled from "styled-components";
import {BsFillChatLeftTextFill} from "react-icons/bs";
import {BiCaretDown} from "react-icons/bi";
import {ThemeContext, themes} from './themeContext';
import logo from "../img/logo.svg";

const Navbar = (props) => {
    const StyledLink = styled(Link)`
    display: inline-block;
    position: relative;
    color:white;
    font-size: none;
    text-decoration: none;
    font-family: "Times New Roman", Times, serif;
    &:hover{
        color:white;
    }
    `;
    const [style1, setStyle1] = ["link-div", "btn", "btn-primary"].join(" ");
    const [style2, setStyle2] = ["link-div", "btn", "btn-primary"].join(" ");
    const [style3, setStyle3] = ["link-div", "btn", "btn-primary"].join(" ");
    const [style4, setStyle4] = ["link-div", "btn", "btn-primary"].join(" ");
    const handleClick = (e) => {
        setStyle1([style1, "current"].join(""))
    }
    return (
        <div>
            <div className="navglobal">
                <div className="logo navbar">
                    <Link to="/"><img className="applogo" src={logo}/></Link>
                </div>
                <div className="navcontainer2 navbar">
                <div className={style4}><StyledLink to ="/home" className="nav-item">HomePage</StyledLink></div>
                </div>
                <div className="navcontainer navbar">
                    <div className={style1}><StyledLink to ="/home" onClick={handleClick} className="nav-item">Contact</StyledLink></div>
                    <div className={style2}><StyledLink to ="/signin" className="nav-item">Sign in</StyledLink></div>
                    <div className={style3}><StyledLink to ="/posts" onClick={handleClick} className="nav-item">Posts</StyledLink></div>
                </div>
            </div>
        </div>
    )
}

export default Navbar;
