import react from "react";
import { useState, useEffect } from "react";

const SignIn = () => { 
    const [signIn, setSignIn] = useState(true);
    const handleChange = () => {
        setSignIn(false)
    }
    return (
        <form className="login form-group">
            <h1 className="title">{signIn ? "Sign in":"Sign up"}</h1>
                {
                    SignIn ? (
                        <div className="inputs">
                            Username : <input className="text form-control" type="text"/><br/><br/>
                            Password : <input className="password form-control" type="password"/>
                            <button onClick={handleChange}>Don't have an account ? Sign up !</button>
                        </div>
                    ) : (
                        <div className="inputs">
                            Username : <input className="text" type="text"/><br/><br/>
                            Password : <input className="password" type="password"/>
                        </div>
                        )
                }
        </form>
    )
}

export default SignIn;
