import react from "react";
import { useState, useEffect } from "react";
import MessageUnit from "./messageUnite";
import axios from "axios";

const DiscussionContent = (props) => {
    const [data, setData] = useState();
    const [fdata, setFdata] = useState();
    async function getData(){
        axios.get("http://localhost:3000/messages/")
        .then(
            response=>{
                setData(response.data);
            }
        )
        data.forEach((item) => {
            console.log(item)
        })
    };
    useEffect(() => {
        getData()
    }, [])
    const [message, setMessage] = useState("");
    const handleChange = (e) =>{
        setMessage(e.target.value);
    };
    return(
        <div className="discussion-content">
            <div className="discussion-header bg-dark">
                Name of the discussion
            </div>
            <div className="discussion-render">
                <MessageUnit/>
            </div>
            <div className="discussion-input bg-dark">
                <input className="discussion-inputbar form-control" type="text" onChange={handleChange}></input>
            </div>
        </div>
    )
}

export default DiscussionContent;
